function Fun() {
  let [data, modifyData] = useState(0);

  function increment() {
    modifyData(data + 1);
  }

  function decrement() {
    modifyData(data - 1);
  }

  function reset() {
    modifyData(0);
  }


  let form = {
    firstname: 'yoga',
    lastname: 'anand'
  }
  return (
    <>
      <h1>{data}</h1>
      <button onClick={increment}>+1</button>
      <button onClick={decrement}>-1</button>
      <button onClick={reset}>reset</button>
    </>
  );
}

export default Fun;
